import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UploadFileComponent = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadStatus, setUploadStatus] = useState('');
  const [uploadedImages, setUploadedImages] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Récupérer la liste des images téléversées lors du chargement initial
    fetchUploadedImages();
  }, []);

  const fetchUploadedImages = () => {
    axios.get('http://localhost:3100/api/uploaded-images')
      .then(res => {
        setUploadedImages(res.data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des images téléversées:', error);
      });
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);

      axios.post('http://localhost:3100/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round((progressEvent.loaded / progressEvent.total) * 100);
          setUploadProgress(progress);
        }
      })
        .then(res => {
          setUploadStatus('Image téléversée avec succès.');
          // Actualiser la liste des images après le téléversement
          fetchUploadedImages();
          navigate('/etudiant');
        })
        .catch(error => {
          console.error('Erreur lors du téléversement de l\'image:', error);
          setUploadStatus('Erreur lors du téléversement de l\'image.');
        });
    }
  };

  const handleBack = () => {
    navigate('/Admin_aff');
  };

  return (
    <div>
      <h2>Téléverser une image</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Téléverser</button>
      {uploadProgress > 0 && <p>Progression: {uploadProgress}%</p>}
      {uploadStatus && <p>{uploadStatus}</p>}
      <h2>Images téléversées</h2>
      <ul>
        {uploadedImages.map(image => (
          <li key={image.id}>
            <img src={image.url} alt={image.name} />
          </li>
        ))}
      </ul>
      <button onClick={handleBack}>Retour</button>
    </div>
  );
};

export default UploadFileComponent;
