import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UploadFileComponent = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadStatus, setUploadStatus] = useState('');
  const [imageError, setImageError] = useState('');
  const navigate = useNavigate();

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      validateImage(file);
    }
  };

  const validateImage = (file) => {
    const img = new Image();
    img.src = URL.createObjectURL(file);

    img.onload = () => {
      const width = img.width;
      const height = img.height;

      if (width === 800 && height === 600) { // Remplacez ces valeurs par les dimensions standard souhaitées
        setSelectedFile(file);
        setImageError('');
      } else {
        setImageError('L\'image doit avoir des dimensions de 800x600 pixels.');
        setSelectedFile(null);
      }

      URL.revokeObjectURL(img.src); // Libère la mémoire utilisée par l'objet URL
    };

    img.onerror = () => {
      setImageError('Erreur lors du chargement de l\'image.');
      setSelectedFile(null);
    };
  };

  const handleUpload = () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);

      axios.post('http://localhost:3100/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round((progressEvent.loaded / progressEvent.total) * 100);
          setUploadProgress(progress);
        }
      })
        .then(res => {
          setUploadStatus('Image téléversée avec succès.');
          navigate('/etudiant');
        })
        .catch(error => {
          console.error('Erreur lors du téléversement de l\'image:', error);
          setUploadStatus('Erreur lors du téléversement de l\'image.');
        });
    }
  };

  const handleBack = () => {
    navigate('/Admin_aff');
  };

  return (
    <div>
      <h2>Téléverser une image</h2>
      <input type="file" onChange={handleFileChange} />
      {imageError && <p style={{ color: 'red' }}>{imageError}</p>}
      <button onClick={handleUpload} disabled={!selectedFile}>Téléverser</button>
      {uploadProgress > 0 && <p>Progression: {uploadProgress}%</p>}
      {uploadStatus && <p>{uploadStatus}</p>}
      <button onClick={handleBack}>Retour</button>
    </div>
  );
};

export default UploadFileComponent;
