import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UploadFileComponent = () => {
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadStatus, setUploadStatus] = useState('');
  const [imageList, setImageList] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Récupérer la liste des images téléversées lorsque le composant est monté
    axios.get('http://localhost:3100/api/images')
      .then(res => {
        setImageList(res.data); // Supposant que la réponse de l'API est un tableau d'objets représentant les images
      })
      .catch(error => {
        console.error('Erreur lors de la récupération de la liste des images:', error);
      });
  }, []);

  const handleBack = () => {
    navigate('/Admin_aff');
  };

  return (
    <div>
      <h2>Liste des images téléversées</h2>
      {imageList.length > 0 ? (
        <ul>
          {imageList.map((image, index) => (
            <li key={index}>
              <img src={image.url} alt={image.fileName} />
            </li>
          ))}
        </ul>
      ) : (
        <p>Aucune image téléversée pour le moment.</p>
      )}
      <button onClick={handleBack}>Retour</button>
    </div>
  );
};

export default UploadFileComponent;
